package DisplayDataType;

public class DisplayDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		float b = 10.5f;
		double c = 185.32d;
		char ch1 = 's';
		boolean isMarried = false;
		long amount = 10000;
		short s = 10;
		byte f = 100;
		System.out.println("Integer Value =" +a);
		System.out.println("Float Value =" +b);
		System.out.println("Double Value =" +c);
		System.out.println("char Value =" +ch1);
		System.out.println("boolean Value =" +isMarried);
		System.out.println("Long Value =" +amount);
		System.out.println("Short Value =" +s);
		System.out.println("Byte Value =" +f);
	}

}
