package PrintingStudList;

import java.util.Scanner;

public class PrintingStudList {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] friendsName = {"Shanthi", "Mona", "Vaishu", "Anitha","Pavithra"};
		int[] age = {20,11,12,17,10};
		char[][] grade = {{'A','B','C','D','E'},{'A','B','F','D','E'},
				{'A','B','C','D','F'},{'A','F','C','D','F'},{'A','F','C','D','F'}};
		while(friendsName == null)
		{
		System.out.println(" Student name is " + friendsName[0]);
		System.out.println(" Student Age is " + age[0]);
		System.out.println(" Grade in 1st subject is: " + grade[0]);
		System.out.println(" Grade in 2nd subject is: " + grade[0]);
		System.out.println(" Grade in 3rd subject is: " + grade[0]);
		System.out.println(" Grade in 4th subject is: " + grade[0]);
		System.out.println(" Grade in 5th subject is: " + grade[0]);
		}
	}
	}

